import React, { useRef, useState } from "react";
import { uploadAndRun, fetchFile } from "./api";
import PipelineTabs from "./components/PipelineTabs";

function App() {
  const fileInput = useRef();
  const [status, setStatus] = useState("Idle");
  const [notebook, setNotebook] = useState("");
  const [readme, setReadme] = useState("");
  const [notebookPath, setNotebookPath] = useState("");
  const [readmePath, setReadmePath] = useState("");

  const handleUploadAndRun = async (e) => {
    e.preventDefault();
    setNotebook("");
    setReadme("");
    setStatus("Processing...");
    const file = fileInput.current.files[0];
    if (!file) return;
    try {
      const res = await uploadAndRun(file);
      setNotebookPath(res.notebook);
      setReadmePath(res.readme);
      // Fetch file contents for preview
      const [nb, rd] = await Promise.all([
        fetchFile(res.notebook),
        fetchFile(res.readme),
      ]);
      setNotebook(nb);
      setReadme(rd);
      setStatus("Done!");
    } catch (err) {
      setStatus("Error: " + err.message);
    }
  };

  const handleDownload = (type) => {
    const path = type === "notebook" ? notebookPath : readmePath;
    window.open(`http://localhost:8000/${path}`, "_blank");
  };

  return (
    <div style={{
      maxWidth: 700,
      margin: "40px auto",
      background: "#22272e",
      border: "1px solid #373e47",
      borderRadius: 8,
      boxShadow: "0 2px 8px rgba(0,0,0,0.16)",
      padding: 32,
      color: "#adbac7"
    }}>
      <h2 style={{ fontWeight: 700, color: "#cdd9e5" }}>AI Data Science Pipeline</h2>
      <form onSubmit={handleUploadAndRun} style={{ marginBottom: 24 }}>
        <input
          type="file"
          accept=".csv"
          ref={fileInput}
          style={{
            padding: 8,
            border: "1px solid #444c56",
            borderRadius: 4,
            marginRight: 12,
            background: "#2d333b",
            color: "#adbac7"
          }}
          required
        />
        <button
          type="submit"
          style={{
            background: "#347d39",
            color: "#fff",
            border: "none",
            padding: "10px 24px",
            borderRadius: 6,
            fontWeight: 600,
            cursor: "pointer"
          }}
        >
          Upload & Run
        </button>
      </form>
      <div style={{ marginBottom: 16, color: status.startsWith("Error") ? "#f47067" : "#768390" }}>Status: {status}</div>
      {(notebook || readme) && (
        <PipelineTabs
          notebook={notebook}
          readme={readme}
          onDownload={handleDownload}
          darkMode={true}
        />
      )}
    </div>
  );
}

export default App;
