import React, { useRef, useState } from "react";
import { uploadAndRun, fetchFile } from "./api";
import PipelineTabs from "./components/PipelineTabs";
import LandingPage from "./components/LandingPage";

function GlassShapes() {
  return (
    <div style={{ position: "absolute", inset: 0, zIndex: 0, pointerEvents: "none" }}>
      <div style={{
        position: "absolute", left: "10%", top: "10%", width: 220, height: 220,
        background: "#b3d1f7", filter: "blur(80px)", opacity: 0.45, borderRadius: "50%"
      }} />
      <div style={{
        position: "absolute", right: "8%", top: "30%", width: 180, height: 180,
        background: "#cbe5ff", filter: "blur(70px)", opacity: 0.38, borderRadius: "40% 60% 60% 40% / 60% 40% 60% 40%"
      }} />
      <div style={{
        position: "absolute", left: "20%", bottom: "8%", width: 260, height: 120,
        background: "#a0c4f6", filter: "blur(90px)", opacity: 0.32, borderRadius: "60% 40% 40% 60% / 40% 60% 60% 40%"
      }} />
      <div style={{
        position: "absolute", right: "18%", bottom: "10%", width: 140, height: 140,
        background: "#b3d1f7", filter: "blur(60px)", opacity: 0.30, borderRadius: "50%"
      }} />
    </div>
  );
}

function App() {
  const [showLanding, setShowLanding] = useState(true);
  const fileInput = useRef();
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("Idle");
  const [notebook, setNotebook] = useState("");
  const [readme, setReadme] = useState("");
  const [notebookPath, setNotebookPath] = useState("");
  const [readmePath, setReadmePath] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setNotebook("");
    setReadme("");
    setStatus("Idle");
  };

  const handleRemoveFile = () => {
    setFile(null);
    setNotebook("");
    setReadme("");
    setStatus("Idle");
    if (fileInput.current) fileInput.current.value = "";
  };

  const handleUploadAndRun = async () => {
    setStatus("Processing...");
    try {
      const res = await uploadAndRun(file);
      setNotebookPath(res.notebook);
      setReadmePath(res.readme);
      const [nb, rd] = await Promise.all([
        fetchFile(res.notebook),
        fetchFile(res.readme),
      ]);
      setNotebook(nb);
      setReadme(rd);
      setStatus("Done!");
    } catch (err) {
      setStatus("Error: " + err.message);
    }
  };

  const handleDownload = (type) => {
    const path = type === "notebook" ? notebookPath : readmePath;
    window.open(`http://localhost:8000/${path}`, "_blank");
  };

  if (showLanding) {
    return <LandingPage onStart={() => setShowLanding(false)} />;
  }

  return (
    <div style={{
      position: "relative",
      minHeight: "100vh",
      background: "radial-gradient(ellipse at center, #cbe5ff 0%, #b3d1f7 60%, #a0c4f6 100%)",
      overflow: "hidden",
      fontFamily: "Inter, system-ui, sans-serif"
    }}>
      <GlassShapes />
      <div style={{
        maxWidth: 700,
        margin: "60px auto 0 auto",
        background: "rgba(255,255,255,0.45)",
        border: "1px solid #b3d1f7",
        borderRadius: 32,
        boxShadow: "0 8px 64px 0 rgba(80,120,255,0.10)",
        padding: 36,
        color: "#222",
        position: "relative",
        zIndex: 1,
        textAlign: "center"
      }}>
        <div style={{ fontSize: 18, color: "#347d39", fontWeight: 700, letterSpacing: 2, marginBottom: 8 }}>
           DataPilot.
        </div>
        <div style={{ fontSize: 26, fontWeight: 600, color: "#222", marginBottom: 10, letterSpacing: 0.5 }}>
          Upload your CSV and get a ready-to-use Jupyter notebook in seconds.
        </div>
        <div style={{ fontSize: 17, color: "#444", marginBottom: 32, fontWeight: 400 }}>
          1. Click <b>Choose CSV File</b> to select your data.<br />
          2. Click <b>Upload & Run</b> to generate your notebook and README.<br />
          3. Preview or download your results instantly.
        </div>
        <div style={{ marginBottom: 24, display: "flex", alignItems: "center", justifyContent: "center" }}>
          <input
            type="file"
            accept=".csv"
            ref={fileInput}
            style={{ display: "none" }}
            onChange={handleFileChange}
          />
          <input
            type="text"
            value={file ? file.name : ""}
            placeholder="No file chosen"
            readOnly
            style={{
              flex: 1,
              background: "#eaf3fb",
              color: "#222",
              border: "1px solid #b3d1f7",
              borderRadius: 4,
              padding: "10px 12px",
              marginRight: 12,
              fontSize: 16,
              outline: "none",
              minWidth: 180
            }}
          />
          {!file && (
            <button
              onClick={() => fileInput.current.click()}
              style={{
                background: "#347d39",
                color: "#fff",
                border: "none",
                padding: "10px 24px",
                borderRadius: 24,
                fontWeight: 600,
                cursor: "pointer",
                marginRight: 0,
                fontSize: 16
              }}
            >
              Choose CSV File
            </button>
          )}
          {file && (
            <>
              <button
                onClick={handleUploadAndRun}
                style={{
                  background: "#347d39",
                  color: "#fff",
                  border: "none",
                  padding: "10px 24px",
                  borderRadius: 24,
                  fontWeight: 600,
                  cursor: "pointer",
                  marginRight: 8,
                  fontSize: 16
                }}
              >
                Upload & Run
              </button>
              <button
                onClick={handleRemoveFile}
                style={{
                  background: "transparent",
                  color: "#f47067",
                  border: "none",
                  fontSize: 22,
                  cursor: "pointer",
                  lineHeight: 1,
                  marginLeft: 4
                }}
                title="Remove file"
              >
                &times;
              </button>
            </>
          )}
        </div>
        <div style={{ marginBottom: 16, color: status.startsWith("Error") ? "#f47067" : "#347d39", fontWeight: 500, fontSize: 16 }}>{status}</div>
        {(notebook || readme) && (
          <PipelineTabs
            notebook={notebook}
            readme={readme}
            onDownload={handleDownload}
            darkMode={false}
          />
        )}
      </div>
    </div>
  );
}

export default App;
