import React, { useState } from "react";
import NotebookPreview from "./NotebookPreview";

export default function PipelineTabs({ notebook, readme, onDownload }) {
  const [tab, setTab] = useState("notebook");

  return (
    <div style={{ marginTop: 24 }}>
      <div style={{
        display: "flex",
        borderBottom: "1px solid #e1e4e8",
        background: "#f6f8fa"
      }}>
        <button
          style={{
            flex: 1,
            padding: "12px 0",
            border: "none",
            background: tab === "notebook" ? "#fff" : "transparent",
            fontWeight: tab === "notebook" ? "bold" : "normal",
            cursor: "pointer"
          }}
          onClick={() => setTab("notebook")}
        >
          Notebook
        </button>
        <button
          style={{
            flex: 1,
            padding: "12px 0",
            border: "none",
            background: tab === "readme" ? "#fff" : "transparent",
            fontWeight: tab === "readme" ? "bold" : "normal",
            cursor: "pointer"
          }}
          onClick={() => setTab("readme")}
        >
          README
        </button>
      </div>
      <div style={{
        background: "#fff",
        border: "1px solid #e1e4e8",
        borderTop: "none",
        minHeight: 200,
        padding: 16,
        fontFamily: "monospace",
        whiteSpace: "pre-wrap",
        overflowX: "auto"
      }}>
        {tab === "notebook"
          ? <NotebookPreview notebookJSON={notebook} />
          : <div style={{ whiteSpace: "pre-wrap" }}>{readme}</div>
        }
      </div>
      <div style={{ marginTop: 12, textAlign: "right" }}>
        <button
          style={{
            marginRight: 8,
            background: "#2ea44f",
            color: "#fff",
            border: "none",
            padding: "8px 16px",
            borderRadius: 6,
            cursor: "pointer"
          }}
          onClick={() => onDownload(tab)}
        >
          Download {tab === "notebook" ? "Notebook" : "README"}
        </button>
      </div>
    </div>
  );
}
